import React from "react";

function Note(props) {

    const [noteTitle, setNoteTitle] = React.useState("");
    const [noteContent, setNoteContent] = React.useState("");

    function handleTitleChange(event) {
        const newChange = event.target.value;
        setNoteTitle(newChange);
    }

    function handleContentChange(event) {
        const newChange = event.target.value;
        setNoteContent(newChange);
    }

    return (
        <div className="note">
            <h1 onChange={handleTitleChange} value={noteTitle}>
                {props.title}
            </h1>
            <p onChange={handleContentChange} value={noteContent}>
                {props.content}
            </p>
        </div>
    )
}

export default Note;
